# Section 02-1
# 파이썬 크롤링 기초
# urllib 사용법 및 기본 스크래핑

import urllib.request as req

# 파일 url

img_url = 'https://i.pinimg.com/originals/4b/4c/7b/4b4c7b3800e40f38170a3473b93eae04.png'
img_url2 = 'data:image/png;base64,'
html_url = 'https://www.naver.com'

# 다운받을 경로

save_path1 = 'ogu.jpg'
save_path2 = 'naver.html'


# 예외 처리

try:
    file1,header1 = req.urlretrieve(img_url2,save_path1)
    file2,header2 = req.urlretrieve(html_url,save_path2)
except Exception as e:
    print('Download Failed')
    print(e)
else:
    print(header1)
    print(header2)

    # 다운로드 받은 파일 정보
    print('Filename1 {}'.format(file1))
    print('Filename2 {}'.format(file2))
    print()

    # 성공

    print('Dowload Succeed')